import { NextUIProvider, globalCss } from '@nextui-org/react'

const globalStyles = globalCss({
  body: { margin: 0, background: 'red', height: '100vh' }
})

function MyApp({ Component, pageProps }) {
  return (
    <NextUIProvider>
      {globalStyles()}
      <Component {...pageProps} />
    </NextUIProvider>
  )
}

export default MyApp
